import Listings from "@/components/Listings";
import { useUserStore } from "@/stores/user";
import { useSearchStore } from "@/stores/search";
import { storeToRefs } from "pinia";
export default {
  name: 'Home',
  components: {
    Listings
  },
  data() {
    return {
      jwt: '',
      listings: [],
      searchStore: useSearchStore()
    };
  },
  async created() {
    this.listings = await this.getListings();
    await this.searchStore.$subscribe(async (mutation, state) => {
      const ls = await this.getListings();
      this.listings = ls.filter(l => l.attributes.title.includes(state.search) && (state.category === '' || l.attributes.category === state.category));
    });
  },
  methods: {
    async getListings() {
      const res = await fetch("http://localhost:1337/api/listings?populate=images,category");
      const response = await res.json();
      console.log(response);
      return response.data;
    }
  },
  mounted() {
    try {
      const userStore = useUserStore();
      this.jwt = userStore.jwt;
    } catch (err) {}
  }
};
